import React, { useState } from 'react';
import { useParams } from "react-router-dom";
import { useUser } from "../contexts/UserContext";
import './TeacherDashboard.css'; // Importing the CSS file
import QuestionnaireList from "../components/QuestionnaireList";
// Interfaces
interface User {
  id: string;
  name: string;
  role: string;
  faculty: string;
}

interface Question {
  id: string;
  question: string;
  options: string[];
  correctAnswer: number;
}

interface Questionnaire {
  id: string;
  title: string;
  subject: string;
  questions: Question[];
  status: 'draft' | 'submitted' | 'approved' | 'rejected';
  submittedAt?: string;
  teacherId: string;
}

interface TeacherDashboardProps {
  user: User;
  onLogout: () => void;
}

const TeacherDashboard: React.FC<TeacherDashboardProps> = ({ user, onLogout }) => {
const { faculty } = useParams();
  const {  setUser } = useUser();
    
        console.log("utilisateur connecte :",user,setUser);
  const [questionnaires, setQuestionnaires] = useState<Questionnaire[]>([]);
  const [currentQuestionnaire, setCurrentQuestionnaire] = useState<Questionnaire | null>(null);
  const [isCreatingNew, setIsCreatingNew] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  const showToast = (title: string, description: string) => {
    alert(`${title}: ${description}`);
  };

  const createNewQuestionnaire = () => {
    const newQuestionnaire: Questionnaire = {
      id: Date.now().toString(),
      title: '',
      subject: '',
      questions: [],
      status: 'draft',
      teacherId: user.id
    };
    setCurrentQuestionnaire(newQuestionnaire);
    setIsCreatingNew(true);
  };

  const addQuestion = () => {
    if (!currentQuestionnaire) return;

    const newQuestion: Question = {
      id: Date.now().toString(),
      question: '',
      options: ['', '', '', ''],
      correctAnswer: 0
    };

    setCurrentQuestionnaire({
      ...currentQuestionnaire,
      questions: [...currentQuestionnaire.questions, newQuestion]
    });
  };

  const updateQuestion = (questionId: string, field: keyof Question, value: any) => {
    if (!currentQuestionnaire) return;

    setCurrentQuestionnaire({
      ...currentQuestionnaire,
      questions: currentQuestionnaire.questions.map(q =>
        q.id === questionId ? { ...q, [field]: value } : q
      )
    });
  };

  const updateQuestionOption = (questionId: string, optionIndex: number, value: string) => {
    if (!currentQuestionnaire) return;

    setCurrentQuestionnaire({
      ...currentQuestionnaire,
      questions: currentQuestionnaire.questions.map(q =>
        q.id === questionId
          ? {
              ...q,
              options: q.options.map((opt, idx) => (idx === optionIndex ? value : opt))
            }
          : q
      )
    });
  };

  const removeQuestion = (questionId: string) => {
    if (!currentQuestionnaire) return;

    if (window.confirm("ÃŠtes-vous sÃ»r de vouloir supprimer cette question ?")) {
      setCurrentQuestionnaire({
        ...currentQuestionnaire,
        questions: currentQuestionnaire.questions.filter(q => q.id !== questionId)
      });
    }
  };

  const saveQuestionnaire = () => {
    if (!currentQuestionnaire) return;

    if (!currentQuestionnaire.title.trim() || !currentQuestionnaire.subject.trim()) {
      showToast('Erreur', 'Veuillez remplir le titre et la matiÃ¨re');
      return;
    }

    if (currentQuestionnaire.questions.length === 0) {
      showToast('Erreur', 'Veuillez ajouter au moins une question');
      return;
    }

    const incompleteQuestions = currentQuestionnaire.questions.some(q =>
      !q.question.trim() || q.options.some(opt => !opt.trim())
    );

    if (incompleteQuestions) {
      showToast('Erreur', 'Veuillez complÃ©ter toutes les questions et leurs options');
      return;
    }

    if (isCreatingNew) {
      setQuestionnaires(prev => [...prev, currentQuestionnaire]);
    } else {
      setQuestionnaires(prev =>
        prev.map(q => (q.id === currentQuestionnaire.id ? currentQuestionnaire : q))
      );
    }

    showToast('SuccÃ¨s', 'Questionnaire sauvegardÃ© avec succÃ¨s');
    setCurrentQuestionnaire(null);
    setIsCreatingNew(false);
  };
if (!user || !user.id) {
  showToast("Erreur", "Identifiant de l'utilisateur non disponible.");
  return;
}
const submitQuestionnaire = async (questionnaire: Questionnaire) => {
  console.log("ðŸ›  ReÃ§u dans req.body :", );
  if (!questionnaire.title.trim() || !questionnaire.subject.trim() || !questionnaire.teacherId) {
    showToast("Erreur", "Champs requis manquants (titre, matiÃ¨re ou identifiant enseignant).");
    return;
  }

  if (questionnaire.questions.length === 0) {
    showToast("Erreur", "Veuillez ajouter au moins une question.");
    return;
  }

  try {
    console.log("Utilisateur utilisÃ© pour soumission :", user);
    console.log("ðŸ§  Utilisateur (user) :", user);
    
    const payload = {
      ...questionnaire,
     teacherId: String(user.id),          // âœ… assure-toi que câ€™est une chaÃ®ne
     teacherName: user.name,             // âœ… AJOUT
      status: 'submitted',
      submittedAt: new Date().toISOString()
    };

    console.log("Corps de la requÃªte envoyÃ© :", payload);

    const response = await fetch(`http://localhost:3001/api/questionnaires/submit`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(payload),
    });

    console.log('RÃ©ponse du serveur:', response);

    if (!response.ok) {
      const errorText = await response.text();
      console.error('Erreur lors de la soumission, serveur rÃ©pond:', errorText);
      showToast("Erreur", errorText);
      return;
    }

    setQuestionnaires(prev =>
      prev.map(q => q.id === questionnaire.id ? { ...questionnaire, status: 'submitted', submittedAt: new Date().toISOString() } : q)
    );

    showToast("SuccÃ¨s", "Questionnaire soumis avec succÃ¨s !");
    setCurrentQuestionnaire(null);

  } catch (error) {
    console.error("Erreur dans submitQuestionnaire:", error);
    showToast("Erreur", "Ã‰chec de la soumission du questionnaire.");
  }
};


  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'draft': return <span className="badge draft">Brouillon</span>;
      case 'submitted': return <span className="badge submitted">Soumis</span>;
      case 'approved': return <span className="badge approved">ApprouvÃ©</span>;
      case 'rejected': return <span className="badge rejected">RejetÃ©</span>;
      default: return <span>Inconnu</span>;
    }
  };

  const filteredQuestionnaires = questionnaires.filter(q =>
    q.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="dashboard-container">
      <div className="p-6">
      <h2>Bienvenue {user?.name} ðŸ‘‹</h2>
      <p>FacultÃ© : {user?.faculty}</p>
      <QuestionnaireList faculty={user.faculty} />

      {faculty === "informatique" && (
        <div>
          <h2 className="text-lg font-semibold">FacultÃ© dâ€™Informatique</h2>
          <p>Gestion des cours, examens et devoirs pour Informatique.</p>
        </div>
      )}

      {faculty === "polytechnique" && (
        <div>
          <h2 className="text-lg font-semibold">FacultÃ© Polytechnique</h2>
          <p>Organisation des modules et projets dâ€™ingÃ©nierie.</p>
        </div>
      )}

      {faculty === "etudes-de-la-bible" && (
        <div>
          <h2 className="text-lg font-semibold">FacultÃ© Ã‰tudes de la Bible</h2>
          <p>PrÃ©paration des examens bibliques et suivis spirituels.</p>
        </div>
      )}

      {faculty === "medecine" && (
        <div>
          <h2 className="text-lg font-semibold">FacultÃ© de MÃ©decine</h2>
          <p>Suivi des stages cliniques et gestion des examens mÃ©dicaux.</p>
        </div>
      )}

      {faculty === "droit" && (
        <div>
          <h2 className="text-lg font-semibold">FacultÃ© de Droit</h2>
          <p>Conception des examens et suivi des Ã©tudiants en droit.</p>
        </div>
      )}

      {faculty === "sciences-economiques" && (
        <div>
          <h2 className="text-lg font-semibold">FacultÃ© de Sciences Ã‰conomiques</h2>
          <p>Organisation des cours de finance, marketing et Ã©conomie.</p>
        </div>
      )}

      {faculty !== "informatique" &&
        faculty !== "polytechnique" &&
        faculty !== "etudes-de-la-bible" &&
        faculty !== "medecine" &&
        faculty !== "droit" &&
        faculty !== "sciences-economiques" && (
          <div className="text-red-600">
            <p>: <strong>{faculty}</strong></p>
          </div>
      )}
    </div>
      <header className="header">
        <div>
          <h1>Espace Professeur</h1>
          <p>Bienvenue, {user.name}</p>
        </div>
        <button className="logout-btn" onClick={onLogout}>DÃ©connexion</button>
      </header>

      <div className="create-questionnaire">
        <button className="create-btn" onClick={createNewQuestionnaire}>CrÃ©er un nouveau questionnaire</button>
      </div>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Rechercher un questionnaire..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
          className="input-field"
        />
      </div>

      <div className="questionnaires-list">
        <h2>Mes Questionnaires</h2>
        {filteredQuestionnaires.length === 0 ? (
          <p>Aucun questionnaire crÃ©Ã©. Cliquez sur "CrÃ©er un nouveau questionnaire" pour commencer.</p>
        ) : (
          <ul className="questionnaire-items">
            {filteredQuestionnaires.map(q => (
              <li key={q.id} className="questionnaire-item">
                <h3>{q.title}</h3>
                <p>MatiÃ¨re : {q.subject}</p>
                <p>{q.questions.length} question(s)</p>
                <p>Statut : {getStatusBadge(q.status)}</p>
                {q.status === 'draft' && (
                  <>
                    <button className="edit-btn" onClick={() => setCurrentQuestionnaire(q)}>Modifier</button>
                    <button
                      className="submit-btn"
                      onClick={() => submitQuestionnaire(q)}
                      disabled={q.questions.length === 0}
                    >
                      <p>Soumis par : <strong>{user.name}</strong></p>
                      Soumettre
                    </button>
                  </>
                )}
                {q.status === 'submitted' && <p>En attente de validation</p>}
                {q.status === 'approved' && <p className="approved-status">ApprouvÃ©</p>}
                {q.status === 'rejected' && <p className="rejected-status">RejetÃ©</p>}
              </li>
            ))}
          </ul>
        )}
      </div>

      {currentQuestionnaire && (
        <div className="questionnaire-editor">
          <h2>{isCreatingNew ? 'Nouveau Questionnaire' : 'Modifier le Questionnaire'}</h2>
          <div>
            <label>Titre de l'examen</label>
            <input
              type="text"
              value={currentQuestionnaire.title}
              onChange={e => setCurrentQuestionnaire({ ...currentQuestionnaire, title: e.target.value })}
              placeholder="Ex: Examen de MathÃ©matiques"
              className="input-field"
            />
          </div>
          <div>
            <label>MatiÃ¨re</label>
            <input
              type="text"
              value={currentQuestionnaire.subject}
              onChange={e => setCurrentQuestionnaire({ ...currentQuestionnaire, subject: e.target.value })}
              placeholder="Ex: MathÃ©matiques"
              className="input-field"
            />
          </div>
          <div>
            <h3>Questions</h3>
            <button className="add-question-btn" onClick={addQuestion}>Ajouter une question</button>
            {currentQuestionnaire.questions.length === 0 && (
              <p className="no-questions">Aucune question ajoutÃ©e</p>
            )}
            {currentQuestionnaire.questions.map((question, idx) => (
              <div key={question.id} className="question-card">
                <div className="question-header">
                  <strong>Question {idx + 1}</strong>
                  <button className="remove-question-btn" onClick={() => removeQuestion(question.id)}>Supprimer</button>
                </div>
                <textarea
                  value={question.question}
                  onChange={e => updateQuestion(question.id, 'question', e.target.value)}
                  placeholder="Saisissez la question"
                  className="question-textarea"
                />
                <div>
                  <strong>Options de rÃ©ponse :</strong>
                  {question.options.map((opt, optIdx) => (
                    <div key={optIdx} className="option-row">
                      <input
                        type="radio"
                        name={`correct-${question.id}`}
                        checked={question.correctAnswer === optIdx}
                        onChange={() => updateQuestion(question.id, 'correctAnswer', optIdx)}
                      />
                      <input
                        type="text"
                        value={opt}
                        onChange={e => updateQuestionOption(question.id, optIdx, e.target.value)}
                        placeholder={`Option ${optIdx + 1}`}
                        className="option-input"
                      />
                      {question.correctAnswer === optIdx && (
                        <span className="correct-indicator">(Correct)</span>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
          <div className="action-buttons">
            <button className="cancel-btn" onClick={() => { setCurrentQuestionnaire(null); setIsCreatingNew(false); }}>
              Annuler
            </button>
            <button className="save-btn" onClick={saveQuestionnaire}>Sauvegarder</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default TeacherDashboard;
