import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useUser } from "../contexts/UserContext";
import { Link } from "react-router-dom";

import {
  Card, CardContent, CardDescription, CardHeader, CardTitle
} from "../components/ui/card";
import { Button } from "../components/ui/button";
import { Input } from "../components/ui/input";
import { Label } from "../components/ui/label";
import {
  Select, SelectContent, SelectItem, SelectTrigger, SelectValue
} from "../components/ui/select";

type UserRole = "student" | "teacher" | "admin";

const faculties = [
  "Informatique",
  "Gestion",
  "Sciences de l’ingénieur",
  "Lettres et sciences humaines",
  "Sciences de la santé",
  "Polytechnique",
  "Études de la Bible",
  "Agronomie",
  "Droit",
  "Économie",
];

const Index = () => {
  const { setUser } = useUser();
  const navigate = useNavigate();

  const [loginForm, setLoginForm] = useState({
    id:"",
    matricule: '',
    password: '',
    role: '' as UserRole | '',
    faculty: ''
  });

  const [loginError, setLoginError] = useState("");

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoginError("");

    try {
      const response = await axios.post("http://localhost:3001/api/auth/login", {
        id :"",
        matricule: loginForm.matricule,
        password: loginForm.password,
        faculty: loginForm.faculty,
        role: loginForm.role
      });

      const user = response.data.user;

      if (user.role !== loginForm.role) {
        setLoginError("Rôle incorrect pour cet utilisateur.");
        return;
      }

      setUser(user); // 🔐 Stocke l'utilisateur globalement

      const facSlug = user.faculty.toLowerCase().replace(/\s+/g, "-");
      navigate(`/dashboard/${facSlug}/${user.role}`);
    } catch (error) {
      console.error("Erreur de connexion :", error);
      setLoginError("Nom, mot de passe ou faculté incorrect.");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <div className="w-full max-w-2xl">
        <Card className="shadow-lg bg-white p-6 rounded-xl">
          <CardHeader>
            <CardTitle className="text-indigo-700 text-2xl text-center">Connexion</CardTitle>
            <CardDescription className="text-center">Connexion via API</CardDescription>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleLogin} className="space-y-4">

              {/* Rôle */}
              <div>
                <Label>Rôle</Label>
                <Select
                  value={loginForm.role}
                  onValueChange={(value: UserRole) => setLoginForm({ ...loginForm, role: value })}
                  required
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Choisir un rôle" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="student">Étudiant</SelectItem>
                    <SelectItem value="teacher">Professeur</SelectItem>
                    <SelectItem value="admin">Administration</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {/* Faculté */}
              <div>
                <Label>Faculté</Label>
                <Select
                  value={loginForm.faculty}
                  onValueChange={(value: string) => setLoginForm({ ...loginForm, faculty: value })}
                  required
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Choisir une faculté" />
                  </SelectTrigger>
                  <SelectContent>
                    {faculties.map((f) => (
                      <SelectItem key={f} value={f}>{f}</SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              {/* Matricule */}
              <div>
                <Label>Matricule</Label>
                <Input
                  type="text"
                  value={loginForm.matricule}
                  onChange={(e) => setLoginForm({ ...loginForm, matricule: e.target.value })}
                  required
                />
              </div>

              {/* Mot de passe */}
              <div>
                <Label>Mot de passe</Label>
                <Input
                  type="password"
                  value={loginForm.password}
                  onChange={(e) => setLoginForm({ ...loginForm, password: e.target.value })}
                  required
                />
              </div>

              {/* Erreur */}
              {loginError && <p className="text-red-500 text-sm">{loginError}</p>}

              {/* Bouton */}
              <Button type="submit" className="w-full bg-indigo-700 text-white">
                Se connecter
              </Button>

              {/* Lien inscription */}
              <div className="mt-4 text-center text-sm">
                <p>
                  Pas encore de compte ?{" "}
                  <a href="/register" className="text-indigo-600 font-semibold underline">
                    Créer un compte
                  </a>
                </p>
              </div>
            </form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
};


export default Index;
