import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

import { ExamProvider } from "./contexts/ExamContext";
import { UserProvider, useUser } from "./contexts/UserContext";

import Index from "./pages/Index";
import Register from "./pages/Register";
import StudentDashboard from "./pages/StudentDashboard";
import TeacherDashboard from "./pages/TeacherDashboard";
import AdminDashboard from "./pages/AdminDashboard";
//import ExamPage from "./pages/ExamPage";
import LoginProf from "./pages/Login";

// Un composant intermédiaire pour injecter `user` et `logout` aux composants protégés
const AppRoutes: React.FC = () => {
  const { user, logout } = useUser();

  return (
    <Routes>
      <Route path="/" element={<Index />} />
      <Route path="/register" element={<Register />} />
<Route path="/login/professeur" element={<LoginProf />} />
      {/* Dashboards */}
      <Route
        path="/dashboard/:faculty/student"
        element={<StudentDashboard user={user} onLogout={logout} />}
      />
      <Route
        path="/dashboard/:faculty/teacher"
        element={<TeacherDashboard user={user} onLogout={logout} />}
      />
      <Route
        path="/dashboard/:faculty/admin"
        element={<AdminDashboard user={user} onLogout={logout} />}
      />

      {/* Page d'examen */}
      <Route
        path="/exam/:id"
        element={<ExamPage user={user} onLogout={logout} />}
      />
    </Routes>
  );
};

const App: React.FC = () => {
  return (
    <UserProvider>
      <ExamProvider>
        <Router>
          <AppRoutes />
        </Router>
      </ExamProvider>
    </UserProvider>
  );
};

export default App;
