import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { Input } from "../components/ui/input";
import { Button } from "../components/ui/button";
import { Label } from "../components/ui/label";

export default function PlanifierExamen() {
  const location = useLocation();
  const navigate = useNavigate();

  const selectedQuestionnaire = location.state?.questionnaire;

  const [examForm, setExamForm] = useState({
    title: "",
    subject: "",
    duration: 60,
    startTime: "",
    endTime: "",
  });

  const scheduleExam = () => {
    // logiques pour envoyer au backend ici
    console.log("Planning exam with:", examForm);
    navigate("/admin"); // ou autre page après planification
  };

  return (
    <div className="p-6 max-w-3xl mx-auto space-y-4">
      <h2 className="text-2xl font-bold text-center mb-4">Planifier un Examen</h2>

      <p className="text-sm text-gray-600 mb-4">
        Questionnaire sélectionné : <strong>{selectedQuestionnaire?.title || "Aucun"}</strong>
      </p>

      <div className="space-y-4">
        <div>
          <Label>Titre</Label>
          <Input
            value={examForm.title}
            onChange={(e) => setExamForm({ ...examForm, title: e.target.value })}
          />
        </div>

        <div>
          <Label>Matière</Label>
          <Input
            value={examForm.subject}
            onChange={(e) => setExamForm({ ...examForm, subject: e.target.value })}
          />
        </div>

        <div>
          <Label>Durée (minutes)</Label>
          <Input
            type="number"
            min="1"
            value={examForm.duration}
            onChange={(e) =>
              setExamForm({ ...examForm, duration: parseInt(e.target.value) || 60 })
            }
          />
        </div>

        <div>
          <Label>Heure de début</Label>
          <Input
            type="datetime-local"
            value={examForm.startTime}
            onChange={(e) => setExamForm({ ...examForm, startTime: e.target.value })}
          />
        </div>

        <div>
          <Label>Heure de fin</Label>
          <Input
            type="datetime-local"
            value={examForm.endTime}
            onChange={(e) => setExamForm({ ...examForm, endTime: e.target.value })}
          />
        </div>

        <div className="flex justify-between pt-4">
          <Button variant="outline" onClick={() => navigate(-1)}>Annuler</Button>
          <Button onClick={scheduleExam}>Planifier</Button>
        </div>
      </div>
    </div>
  );
}
